mapper.select:
SELECT t.id,t.name,t.className,t.type,t.description,t.createTime FROM jeegem_TASK_LOG t WHERE 1=1 

 	AND t.id=#{id}


 	AND t.type=#{type}

  order by t.createTime desc

mapper.delete:
DELETE FROM jeegem_TASK_LOG 
WHERE id IN(

		#{o.id}

	)

mapper.update:
UPDATE jeegem_TASK_LOG 
SET description=#{description},	
WHERE id=#{id}
