mapper.select:
SELECT t.* FROM jeegem_base_role t WHERE 1=1 

 	and t.name=#{name}


 	and t.id=#{id}


 	and t.isValid=#{isValid}

ORDER BY t.createTime

SELECT t.* FROM jeegem_base_role t WHERE 1=1

 	AND t.orgId=#{param.orgId}


 	AND (t.name like CONCAT('%',#{param.keyWord},'%') )


 	AND t.isValid=#{param.isValid}

 ORDER BY t.createTime

mapper.delete:
DELETE FROM jeegem_base_role 
WHERE id in(

		#{o.id}

	)

mapper.update:
UPDATE jeegem_base_role 
SET name =#{name},
	isValid=#{isValid},
	description=#{description},
	updateTime =#{updateTime}
WHERE id=#{id}

mapper.delete:
DELETE from jeegem_base_role_resources 
WHERE role_id in(

		#{o.id}

	)

mapper.select:
SELECT t.* FROM jeegem_base_role t 
WHERE 1=1 

 	and t.id=#{id}
