mapper.select:
SELECT t.* FROM jeegem_BASE_RESOURCES t 
WHERE t.id=#{id}
ORDER BY t.parentId,t.type,t.sort

SELECT t.*,jbr.name as parentName FROM jeegem_BASE_RESOURCES t 
LEFT JOIN jeegem_BASE_RESOURCES jbr 
ON jbr.id=t.parentId
WHERE 1=1 

 	AND t.name=#{name}


 	AND t.parentId=#{parentId}


 	AND t.layer=#{layer}


 	AND t.id=#{id}

ORDER BY t.parentId,t.type,t.sort

