mapper.select:
SELECT t.id, t.loginName, t.roleId, jbr.name as roleName, t.name, t.picUrl, t.email, t.isValid, t.createTime, t.updateTime, t.skin, t.description FROM jeegem_base_account t LEFT JOIN jeegem_BASE_ROLE jbr ON jbr.id=t.roleId WHERE 1=1
and t.loginName=#{loginName}
and t.id=#{accountId}

SELECT t.id, t.loginName, t.roleId, jbr.name as roleName, t.name, t.picUrl, t.email, t.isValid, t.createTime, t.updateTime, t.skin, t.description FROM jeegem_BASE_ACCOUNT t LEFT JOIN jeegem_BASE_ROLE jbr ON jbr.id=t.roleId WHERE 1=1
AND t.loginName LIKE CONCAT('%',#{param.loginName},'%')
AND ( t.loginName LIKE CONCAT('%',#{param.keyWord},'%') OR t.name LIKE CONCAT('%',#{param.keyWord},'%') OR t.email LIKE CONCAT('%',#{param.keyWord},'%') )
AND t.roleId=#{param.roleId}
AND t.isValid=#{param.isValid}
ORDER BY t.createTime desc

mapper.delete:
DELETE FROM jeegem_base_account WHERE id in(

		#{o.accountId}

	)

mapper.select:

SELECT a.* FROM jeegem_base_account a WHERE 1=1 
AND a.isvalid='1' 
AND a.loginname=#{loginName,jdbcType=VARCHAR}

mapper.update:
UPDATE jeegem_base_account 
SET   skin =#{skin}
WHERE id=#{accountId}

UPDATE jeegem_base_account 
SET   name =#{name},
	  email =#{email},
	  updateTime =#{updateTime}
WHERE id=#{accountId}

UPDATE jeegem_base_account 
SET   picUrl =#{picUrl},
	  updateTime =#{updateTime}
WHERE id=#{accountId}

mapper.select:
SELECT COUNT(*)
		  FROM jeegem_base_account t 
WHERE  t.loginName=#{loginName}

mapper.update:
UPDATE jeegem_base_account 
SET   password =#{password},
	  salt =#{salt}
WHERE id=#{accountId}
