mapper.select:
SELECT t.*,t.keyId as itemId FROM WX_MENU t WHERE 1=1 

 	AND t.id=#{id}

 ORDER BY t.pId,t.sort

SELECT t.* FROM WX_EVENT_CLICK t WHERE  
t.keyId=#{itemId}
ORDER BY t.sort

SELECT t.* FROM WX_MENU t WHERE 1=1 

 	AND t.isValid=#{param.isValid}

order by t.createTime desc

mapper.delete:
DELETE FROM WX_MENU 
WHERE id IN(

		#{o.id}

	)
